mapper.select:
select count(user_id)
from member
where user_id = #{id}

select user_id, role, email, name, join_date, location, profile_no
from member
where user_id = #{id} and password = #{password}

select user_id, role, password, email, name, join_date, location, profile_no
from member
where user_id = #{id}

mapper.update:
update member
password = #{password}, email = #{email}, name = #{name}, location = #{location}, profile_no = #{profileNo},
