mapper.select:
select count(user_id)
from member
where user_id = #{userId}

select user_id, password, name, tel, location, join_date
from member
where user_id = #{userId} and password = #{userPassword}

select user_id, name, password, email_id, email_domain, join_date
from member
where user_id = #{userId}

mapper.update:
update member
user_id = #{userId}, password = #{userPassword}, name = #{userName}, tel = #{tel}, location = #{location}, join_date = #{joinDate},
where user_id = #{userId}
